mapper.select:
SELECT COUNT(*)
FROM board
WHERE type = #{type}

SELECT A.seq, A.content, C.name, DATE_FORMAT(A.reg_dt,'%Y-%m-%d') reg_dt, REPLACE(REPLACE(DATE_FORMAT(A.reg_dt,'%p %H:%i'),'AM','오전'),'PM','오후') reg_dt_hms, A.user_id, (SELECT COUNT(*) FROM board_comment_like_count D WHERE A.seq = D.seq AND A.board_seq = D.board_seq) AS like_count, A.reg_dt FROM board_comment A JOIN board B ON A.board_seq = B.seq LEFT JOIN user C ON A.user_id = C.id WHERE B.type = #{type} AND A.board_seq = #{seq}
ORDER BY A.reg_dt DESC
ORDER BY like_count DESC, A.reg_dt DESC
LIMIT #{start_row_num}, #{post_num_base_cnt}

insert.if:
UPDATE board
SET title = #{title}, content = #{content}, update_dt = NOW()
WHERE seq = #{seq}

mapper.update:
UPDATE board
SET pv = pv + 1
WHERE seq = #{seq}

insert.if:
UPDATE board_comment
SET content = #{content}, update_dt = NOW()
WHERE seq = #{seq}

mapper.insert:
UPDATE board_comment set
				 content = #{content}
				 update_dt = NOW()
WHERE seq = #{seq}

mapper.update:
UPDATE board_comment_like_pv set
	content = #{content}
	update_dt = NOW()
WHERE seq = #{seq}
